import React from 'react';
import axios from 'axios';
class Highlights extends React.Component{
    
  
  
  state={news:[]
  
  }
  componentDidMount(){
    axios.get('http://localhost:8084/api/news/newss').then(res=>{
      const news=res.data;
      this.setState({news});
    }
    )
  }
  
  
  
  
  render ()
    {
return(

<div>
<section className="bg0">
  <div className="container" >

    <div id="myCarousel"className="carousel slide carousel-fade" data-ride="carousel" data-interval="false">
      <div className="carousel-inner" id="highlights">
      
       { this.state.news.map(news=>

      <div className="carousel-item active">
          <div className="mask flex-center">
            <div className="container">
              <div className="row align-items-center" style={{background:'#050e61',paddingLeft:'-15px'}}>
                <div className="col-md-7 "><img src={news.photolink}  alt="slide" style={{marginLeft: '-19px'}}/></div>
                <div className="col-md-5 ">
                  <h4> {news.headline}</h4>
                  <p>{news.shortDescription}
                    </p>
                  <a href="#">READ MORE</a> </div>
              </div>
            </div>
          </div>
        </div>
       

    )}
    <a classNameName="carousel-control-prev" href="#myCarousel" role="button" data-slide="prev"> <span classNameName="carousel-control-prev-icon" aria-hidden="true"></span> <span classNameName="sr-only">Previous</span> </a> 
      <a classNameName="carousel-control-next" href="#myCarousel" role="button" data-slide="next"> <span classNameName="carousel-control-next-icon" aria-hidden="true"></span> <span classNameName="sr-only">Next</span> </a>  
  </div>
</div>
</div>
</section>



</div>);
    }
}
export default Highlights;
